var MenuJScripts = function() {

    var globalURLEnlaceme = localStorage.getItem(globalURLEnlaceme);    
    
        var AccountActionsMenuScript = function() {
            
            $('#activeSectionImageProfile').click(function(event){
                //alert('sectionImageProfile');
                $('#sectionImageProfile').slideDown();
                $('#sectionAccount').slideUp();
                $('#sectionProfession').slideUp();
                $('#sectionProjects').slideUp();
                event.preventDefault();
            });
            
            $('#activeSectionAccount').click(function(event){
                $('#sectionImageProfile').slideUp();
                $('#sectionAccount').slideDown();
                $('#sectionProfession').slideUp();
                $('#sectionProjects').slideUp();
                event.preventDefault();
            });
            
            $('#activeSectionProfession').click(function(event){
                $('#sectionImageProfile').slideUp();
                $('#sectionAccount').slideUp();
                $('#sectionProfession').slideDown();
                $('#sectionProjects').slideUp();
                event.preventDefault();
            });
            
            $('#activeSectionProjects').click(function(event){
                $('#sectionImageProfile').slideUp();
                $('#sectionAccount').slideUp();
                $('#sectionProfession').slideUp();
                $('#sectionProjects').slideDown();
                event.preventDefault();
            });            
        };    
    
	return {
		init: function() {
			AccountActionsMenuScript();
		}
	}
}();
jQuery(document).ready(function() {
	MenuJScripts.init()
});