var NotificationsScript = function() {
    
    var BasicNotification = function(type, message) {
        
        toastr.options = {
              "closeButton": true,
              "debug": false,
              "positionClass": "toast-top-center",
              "showDuration": "4000",
              "hideDuration": "1000",
              "timeOut": "8000",
              "extendedTimeOut": "1000",
              "showEasing": "swing",
              "hideEasing": "linear",
              "showMethod": "fadeIn",
              "hideMethod": "fadeOut"
            }
    
        if( type == 'success' ) {
            toastr.success(message,'<i>Success</i>');
        } else if(type == 'error') {
            toastr.error(message,'Error');
        } else if(type == 'warning') {
            toastr.warning(message,'Warning');
        } else if(type == 'info'){
            toastr.info(message,'Information');
        }
        
        
    }
    
	return {
		init: function() {
			BasicNotification();
		}
	}
}();
jQuery(document)
	.ready(function() {
		NotificationsScript.init()
	});